import logo from './logo.svg';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Img,Card, Container, Row,Col } from 'react-bootstrap';
import React,{ useState,useEffect } from 'react';

function App() {
  const [mydata,setData]=useState([]);
  const apiGet=()=>{
    var a=prompt("In Which Category You Want To Explore ? ")
    fetch('https://inshorts.deta.dev/news?category='+a)  //category=sports,entertainment,international,science,politics
    .then((response)=>response.json())
    .then((json)=>{
      // console.log(json),
      setData(json.data);
    });
  };

  useEffect(()=>{
    apiGet();
    const interval=setInterval(()=>{
      apiGet();
    },50000);
    return ()=> clearInterval(interval);

  },[])
  return (
    <div className="App">
      <h1> hello</h1>

      <Container fluid>
      <Row xs={1} md={3} className="g-4">
        {mydata.map(
          (value)=>{
            return(
              <>
              <Col className='container-fluid mt-4'>
              <Card border='success'>
                <Card.Img variant='top' height='350px' width='50%' src={value.imageUrl}></Card.Img>
                <Card.Body>
                  <Card.Title>{value.date}</Card.Title>
                  <Card.Title>{value.content}</Card.Title>
                  <Card.Text></Card.Text>
                </Card.Body>
              </Card>
            </Col>
              </> 
            )
        })}
      </Row>
    </Container>


    </div>
  );
}

export default App;
